@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun KathaScreen(onBack: () -> Unit = {}) {
    val context = LocalContext.current
    val activity = context as? Activity
    val scrollState = rememberScrollState()
    val coroutineScope = rememberCoroutineScope()
    val kathaText = remember { brihaspativarKatha }

    var isScrolling by remember { mutableStateOf(false) }

    // ЁЯФТ Keep screen ON while reading
    DisposableEffect(Unit) {
        activity?.window?.addFlags(WindowManager.LayoutParams.FLAG_KEEP_SCREEN_ON)
        onDispose {
            activity?.window?.clearFlags(WindowManager.LayoutParams.FLAG_KEEP_SCREEN_ON)
        }
    }

    // ЁЯФД Auto Scroll Effect
    LaunchedEffect(isScrolling) {
        if (isScrolling) {
            while (isScrolling) {
                if (scrollState.value < scrollState.maxValue) {
                    scrollState.scrollBy(2f) // ЁЯСИ Adjust scroll speed
                }
                delay(50) // ЁЯСИ Adjust delay (lower = faster)
            }
        }
    }

    Scaffold(
        topBar = {
            CenterAlignedTopAppBar(
                title = {
                    Text(
                        "рдмреГрд╣рд╕реНрдкрддрд┐рд╡рд╛рд░ рд╡реНрд░рдд рдХрдерд╛",
                        style = TextStyle(
                            color = Color.White,
                            fontWeight = FontWeight.Bold,
                            fontSize = 22.sp
                        )
                    )
                },
                colors = TopAppBarDefaults.topAppBarColors(containerColor = Color(0xFFDAA520)),
                navigationIcon = {
                    IconButton(onClick = onBack) {
                        Icon(
                            Icons.Default.ArrowBack,
                            contentDescription = "Back",
                            tint = Color.White
                        )
                    }
                }
            )
        },
        floatingActionButton = {
            Row(
                modifier = Modifier
                    .padding(12.dp)
                    .fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceEvenly
            ) {
                FloatingActionButton(
                    containerColor = Color(0xFF4CAF50),
                    onClick = { isScrolling = true }
                ) {
                    Icon(Icons.Filled.PlayArrow, contentDescription = "Start", tint = Color.White)
                }

                FloatingActionButton(
                    containerColor = Color(0xFFFF9800),
                    onClick = { isScrolling = false }
                ) {
                    Icon(Icons.Filled.Pause, contentDescription = "Pause", tint = Color.White)
                }
            }
        }
    ) { padding ->
        Box(
            modifier = Modifier
                .fillMaxSize()
                .background(
                    Brush.verticalGradient(
                        listOf(
                            Color(0xFFFFF8E1),
                            Color(0xFFFFECB3),
                            Color(0xFFFFF3C2)
                        )
                    )
                )
                .padding(padding)
        ) {
            Column(
                modifier = Modifier
                    .padding(20.dp)
                    .verticalScroll(scrollState)
                    .fillMaxSize(),
                horizontalAlignment = Alignment.CenterHorizontally
            ) {
                Text(
                    text = kathaText,
                    color = Color(0xFF3E2723),
                    fontSize = 19.sp,
                    lineHeight = 28.sp,
                    fontWeight = FontWeight.Medium,
                )
            }
        }
    }
}

